/* ---------------- FEATURE CARD ---------------- */
const FeatureCard = ({ icon, title, description, glowColor }) => (
  <div
    className={`relative rounded-xl bg-[#1a1a1a] border border-white/10 p-6 h-[320px]
    flex flex-col justify-between transition-all duration-300
    ${
      glowColor
        ? "border-[#e40879] shadow-[0_0_30px_-10px_rgba(228,8,121,0.3)]"
        : ""
    }`}
  >
    <div className="flex flex-col gap-4">
      <div className="w-10 h-10 text-white">{icon}</div>
      <h3 className="font-satoshi text-2xl text-white">{title}</h3>
    </div>
    <p className="font-inter font-light text-gray-400 text-sm leading-relaxed">
      {description}
    </p>
  </div>
);

/* ---------------- PROGRESS ---------------- */
const SegmentedProgress = ({ total, active }) => (
  <div className="flex gap-4 mb-12 w-full max-w-lg">
    {Array.from({ length: total }).map((_, idx) => (
      <div
        key={idx}
        className="h-1 flex-1 rounded-full bg-white/10 overflow-hidden"
      >
        <div
          className={`h-full bg-white transition-opacity duration-300 ${
            idx === active ? "opacity-100" : "opacity-0"
          }`}
        />
      </div>
    ))}
  </div>
);

/* ---------------- SECTION ---------------- */
export function Section3() {
  const sliderRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [slidesToShow, setSlidesToShow] = useState(3);

  /* -------- RESPONSIVE SLIDES -------- */
  useEffect(() => {
    const updateSlides = () => {
      setSlidesToShow(window.innerWidth < 640 ? 1 : 3);
    };
    updateSlides();
    window.addEventListener("resize", updateSlides);
    return () => window.removeEventListener("resize", updateSlides);
  }, []);

  const settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow,
    slidesToScroll: 1,
    arrows: false,
    beforeChange: (_, next) => setCurrentSlide(next),
  };

  /* ---------------- GSAP ---------------- */
  useGSAP(() => {
    // Create a context for this component
    const ctx = gsap.context(() => {
      // Select all effectup elements
      const elements = gsap.utils.toArray(".effectup");
      const effectright = gsap.utils.toArray(".effectright");

      elements.forEach((element) => {
        gsap.set(element, { y: 40, opacity: 0 });
        gsap.to(element, {
          y: 0,
          opacity: 1,
          duration: 1,
          ease: "power2.out",
          scrollTrigger: {
            trigger: element,
            start: "top 85%",
            end: "top 45%",
            toggleActions: "play reverse play reverse",
            once: true,
          },
        });
      });

      effectright.forEach((element) => {
        gsap.set(element, { x: 60, opacity: 0 });
        gsap.to(element, {
          x: 0,
          opacity: 1,
          duration: 1.2,
          ease: "power2.out",
          scrollTrigger: {
            trigger: element,
            start: "right 150%",
            end: "right 50%",
            toggleActions: "play reverse play reverse",
            once: true,
          },
        });
      });
    });

    return () => ctx.revert(); // Cleanup
  });

  const maxIndex = features.length - slidesToShow;

  return (
    <section className="bg-[#101010] pt-0 md:pt-24 pb-24 overflow-hidden">
      <div className="max-w-[1152px] mx-auto px-4 flex flex-col gap-16">
        {/* CAROUSEL */}
        <div className="">
          <div className="flex justify-start">
            <SegmentedProgress
              total={features.length - slidesToShow + 1}
              active={Math.min(currentSlide, maxIndex)}
            />
          </div>

          <Slider ref={sliderRef} {...settings} className="effectup">
            {features.map((feature, index) => (
              <div key={index} className="px-2">
                <FeatureCard {...feature} />
              </div>
            ))}
          </Slider>
        </div>

        {/* DEPLOYED ON */}
        <div className="effectup flex flex-col items-center gap-12">
          <h3 className="font-inter text-[#d4d4d4] uppercase tracking-wider">
            Deployed On
          </h3>

          <div className="flex flex-wrap justify-center gap-12 opacity-80">
            <Logos.Azure />
            <Logos.AWS />
            <Logos.IBM />
            <Logos.Google />
            <Logos.StackIT />
          </div>
        </div>
      </div>
    </section>
  );
}

